import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './styles/ProductStyles.css';

const CompanyList = () => {
    const [companies, setCompanies] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:9000/getCompanies')
            .then(response => {
                const fetches = response.data.map(company =>
                    axios.get(`http://localhost:9000/products/company/${company.name}`)
                        .then(res => ({
                            ...company,
                            products: res.data
                        }))
                );
                Promise.all(fetches).then(companiesWithProducts => {
                    setCompanies(companiesWithProducts);
                });
            })
            .catch(error => {
                console.error('Failed to fetch companies', error);
            });
    }, []);

    return (
        <div>
            <h1>Companies</h1>
            <div className="product-container">
                {companies.map(company => (
                    <div key={company.id} className="product-card">
                        <Link to={`/companies/${company.name}`}>
                            <h2>{company.name}</h2>
                        </Link>
                        {company.products.map(product => (
                            <div key={product._id} className="product-info">
                                <img src={product.imageUrl} alt={product.name} className="product-image" />
                                <p>{product.name} - ${product.price}</p>
                                <button onClick={() => navigate(`/product/${product._id}`)}>View Details</button>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CompanyList;
