import React from 'react';
import { useCart } from './CartContext';
import './Apple.css'; // Import CSS file for styles

const Sam = () => {
  const { addToCart } = useCart();
  const productList = [
    { 
      name: 'Tvs', 
      description: 'Latest TV from samsung with cutting-edge features.', 
      image: 'https://content.abt.com/image.php/48d7098da74ec6800ff0216ff8115cc8?image=/media/images/products/BDP_Images/Samsung-Q70C-QLED-Smart-TV-QNXXQ70C-front.jpg&width=150&height=150&canvas' 
    },
    { 
      name: 'Laptops', 
      description: 'Powerful and sleek laptop for work and creativity.', 
      image: 'https://www.billboard.com/wp-content/uploads/2024/04/acer-aspire-laptop-deals.jpg?w=150&h=150&crop=1' 
    },
    { 
      name: 'Tablets', 
      description: 'Versatile tablet for productivity and entertainment.', 
      image: 'https://pisces.bbystatic.com/image2/BestBuy_US/images/products/6490/6490711ld.jpg;maxHeight=150;maxWidth=200' 
    },
    { 
      name: 'Watches', 
      description: 'Smart wearable device for fitness tracking and more.', 
      image: 'https://media.wired.com/photos/612914e967168b68f9ececa6/1:1/w_150%2Cc_limit/Gear-Samsung-Galaxy-Watch4-and-Watch4-Classic-SOURCE-Samsung.jpg' 
    },
    { 
      name: 'Headphones', 
      description: 'Wireless headphones with seamless connectivity.', 
      image: 'https://media.wired.com/photos/633b713191ba135542e3d5c1/1:1/w_150%2Cc_limit/BW-Px8-Headphones-Gear.jpg' 
    },
  ];

  return (
    <div>
      <h1>Samsung Products</h1>
      <div className="product-list">
        {productList.map((product, index) => (
          <div key={index} className="product-item">
            <img src={product.image} alt={product.name} className="product-image" />
            <div className="product-details">
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sam;
