import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProductSort({ category }) {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetchProductsByCategory();
    }, [category]);

    const fetchProductsByCategory = async () => {
        try {
            const response = await axios.get(`http://localhost:9000/getProductsByCategory?category=${category}`);
            setProducts(response.data);
        } catch (error) {
            console.error('Failed to fetch products:', error);
        }
    };

    return (
        <div>
            <h1>Products in {category}</h1>
            {products.length > 0 ? (
                products.map(product => (
                    <div key={product._id} style={{ border: '1px solid gray', padding: '10px', margin: '10px' }}>
                        <img src={product.imageUrl} alt={product.name} style={{ width: '100px', height: '100px' }} />
                        <h2>{product.name}</h2>
                        <p>{product.description}</p>
                        <p>Price: ${product.price}</p>
                        <p>Sold by: {product.soldBy}</p>
                        <p>Category: {product.category}</p>
                    </div>
                ))
            ) : (
                <p>No products found in this category.</p>
            )}
        </div>
    );
}

export default ProductSort;
