import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './styles/ProductStyles.css'; // Import the shared CSS file

const ProductSort = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(false);
    const { category, subcategory } = useParams();

    useEffect(() => {
        setLoading(true);
        const url = subcategory
            ? `http://localhost:9000/products/category/${category}/${subcategory}`
            : `http://localhost:9000/products/category/${category}`;
        axios.get(url)
            .then(response => {
                setProducts(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.log('Error fetching products:', error);
                setLoading(false);
            });
    }, [category, subcategory]);

    if (loading) {
        return <div className="loading-container">Loading...</div>;
    }

    return (
        <div>
            <h1>Products in {category} {subcategory ? `- ${subcategory}` : ''}</h1>
            <div className="product-container">
                {products.length > 0 ? products.map(product => (
                    <div key={product._id} className="product-card">
                        <img src={product.imageUrl} alt={product.name} className="product-image" />
                        <h2>{product.name}</h2>
                        <p>{product.description}</p>
                        <p>Price: ${product.price}</p>
                        <p>Sold by: {product.soldBy}</p>
                        <p>Category: {product.category} - {product.subcategory}</p>
                    </div>
                )) : <p>No products found in this category.</p>}
            </div>
        </div>
    );
};

export default ProductSort;
