import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Dropzone from 'react-dropzone';

function AddProductPage() {
  const [product, setProduct] = useState({ name: '', description: '', price: '', imageUrl: '', soldBy: 'CompanyName' });
  const navigate = useNavigate();

  const onDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];
    const reader = new FileReader();
    reader.onload = () => {
      setProduct({ ...product, imageUrl: reader.result });
    };
    reader.readAsDataURL(file);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProduct({ ...product, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:9000/addProduct', product);
      navigate('/'); // Navigate to the homepage or confirmation page after successful post
    } catch (error) {
      console.error('Failed to add product:', error);
    }
  };

  return (
    <div id="addProductPage">
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <label className="label" htmlFor="name">Product Name</label>
          <input
            className="input"
            type="text"
            id="name"
            name="name"
            value={product.name}
            onChange={handleInputChange}
            required
          />

          <label className="label" htmlFor="description">Description</label>
          <textarea
            className="textarea"
            id="description"
            name="description"
            value={product.description}
            onChange={handleInputChange}
            required
          />

          <label className="label" htmlFor="price">Price ($)</label>
          <input
            className="input"
            type="number"
            id="price"
            name="price"
            placeholder="Price"
            value={product.price}
            onChange={handleInputChange}
            step="0.01"
            required
          />

          <label className="label">Product Image</label>
          <Dropzone onDrop={onDrop}>
            {({ getRootProps, getInputProps }) => (
              <div className="dropzone" {...getRootProps()}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop an image here, or click to select an image</p>
              </div>
            )}
          </Dropzone>

          <button type="submit">Add Product</button>
        </form>
      </div>
    </div>
  );
}

export default AddProductPage;
