import React from 'react';
import { useCart } from './CartContext';
import './styles/Dairy.css'; // Import CSS file for styles

const Dairy = () => {
  const { addToCart } = useCart();
  const dairyList = [
    { 
      name: 'Milk', 
      description: 'A nutritious dairy product rich in calcium and vitamins.', 
      image: 'https://milkyday.com/blog/wp-content/uploads/2020/01/Homogenized-Milk-150x150.jpg' 
    },
    { 
      name: 'Cheese', 
      description: 'A delicious dairy product made from milk.', 
      image: 'https://www.eatthis.com/wp-content/uploads/sites/4/2021/03/american-cheese.jpg?quality=82&strip=all&w=150&h=150&crop=1' 
    },
    { 
      name: 'Yogurt', 
      description: 'A creamy and healthy dairy product with probiotics.', 
      image: 'https://www.cravethegood.com/wp-content/uploads/2021/04/sous-vide-cold-start-yogurt-1-150x150.jpg' 
    },
    { 
      name: 'Butter', 
      description: 'A versatile dairy product used in cooking and baking.', 
      image: 'https://i0.wp.com/www.brownedbutterblondie.com/wp-content/uploads/2020/07/How-to-Brown-Butter-BP-3-1.jpg?resize=150%2C150&ssl=1' 
    },
    { 
      name: 'Cream', 
      description: 'A rich dairy product used in desserts and sauces.', 
      image: 'https://i0.wp.com/www.artfuldishes.com/wp-content/uploads/2018/03/How-to-Make-Homemade-Whipped-Cream-5.jpg?resize=150%2C150&ssl=1' 
    },
  ];

  return (
    <div>
      <h1>Types of Dairy</h1>
      <div className="dairy-list">
        {dairyList.map((dairy, index) => (
          <div key={index} className="dairy-item">
            <img src={dairy.image} alt={dairy.name} className="dairy-image" />
            <div className="dairy-details">
              <h3>{dairy.name}</h3>
              <p>{dairy.description}</p>
              <button onClick={() => addToCart(dairy)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dairy;
