import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ProductSort = () => {
    const [products, setProducts] = useState([]);
    const { category, subcategory } = useParams();

    useEffect(() => {
        const url = subcategory
            ? `http://localhost:9000/products/category/${category}/${subcategory}`
            : `http://localhost:9000/products/category/${category}`;
        axios.get(url)
            .then(response => {
                setProducts(response.data);
            })
            .catch(error => {
                console.log('Error fetching products:', error);
            });
    }, [category, subcategory]);  // React to changes in both category and subcategory

    return (
        <div>
            <h1>Products in {category} {subcategory ? `- ${subcategory}` : ''}</h1>
            <div style={{
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'space-between'
            }}>
                {products.length > 0 ? products.map(product => (
                    <div key={product._id} style={{
                        border: '1px solid gray',
                        padding: '10px',
                        margin: '10px',
                        width: 'calc(33.333% - 20px)',
                        boxSizing: 'border-box'
                    }}>
                        <img src={product.imageUrl} alt={product.name} style={{ width: '100px', height: '100px' }} />
                        <h2>{product.name}</h2>
                        <p>{product.description}</p>
                        <p>Price: ${product.price}</p>
                        <p>Sold by: {product.soldBy}</p>
                        <p>Category: {product.category} - {product.subcategory}</p>
                    </div>
                )) : <p>No products found in this category.</p>}
            </div>
        </div>
    );
};

export default ProductSort;
