import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Import useParams

const ProductSort = () => {
    const [products, setProducts] = useState([]);
    const { category } = useParams(); // Use useParams to get the category from the URL

    useEffect(() => {
        axios.get(`http://localhost:9000/products/category/${category}`)
            .then(response => {
                setProducts(response.data);
            })
            .catch(error => {
                console.log('Error fetching products:', error);
            });
    }, [category]); // Dependency array ensures useEffect runs when category changes

    return (
        <div>
            <h1>Products in {category}</h1>
            <div style={{
                display: 'flex',
                flexWrap: 'wrap', // Allows items to wrap to the next line
                justifyContent: 'space-between' // Distributes space between items
            }}>
                {products.length > 0 ? products.map(product => (
                    <div key={product._id} style={{
                        border: '1px solid gray',
                        padding: '10px',
                        margin: '10px',
                        width: 'calc(33.333% - 20px)', // Adjusts width for three items per row
                        boxSizing: 'border-box'
                    }}>
                        <img src={product.imageUrl} alt={product.name} style={{ width: '100px', height: '100px' }} />
                        <h2>{product.name}</h2>
                        <p>{product.description}</p>
                        <p>Price: ${product.price}</p>
                        <p>Sold by: {product.soldBy}</p>
                        <p>Category: {product.category} - {product.subcategory}</p>
                    </div>
                )) : <p>No products found in this category.</p>}
            </div>
        </div>
    );
};

export default ProductSort;
