import React from 'react';
import { useCart } from './CartContext';
import './Apple.css'; // Import CSS file for styles

const AppleT = () => {
  const { addToCart } = useCart();
  const productList = [
    { 
      name: 'iPhone', 
      description: 'Latest smartphone from Apple with cutting-edge features.', 
      image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/5/55/IPhone_6_Plus_Space_Gray.svg/150px-IPhone_6_Plus_Space_Gray.svg.png' 
    },
    { 
      name: 'MacBook', 
      description: 'Powerful and sleek laptop for work and creativity.', 
      image: 'https://media.wired.com/photos/5fb2cc575c9914713ead03de/1:1/w_150%2Cc_limit/Gear-Apple-MacBook-Air-top-down-SOURCE-Apple.jpg' 
    },
    { 
      name: 'iPad', 
      description: 'Versatile tablet for productivity and entertainment.', 
      image: 'https://media.wired.com/photos/63517171470620c1e69a25e0/1:1/w_150%2Cc_limit/Apple-iPad-10th-gen-blue-gear_CREDIT_-APPLE.jpg' 
    },
    { 
      name: 'Apple Watch', 
      description: 'Smart wearable device for fitness tracking and more.', 
      image: 'https://media.wired.com/photos/650a005a3b7e4d610a1152e8/1:1/w_150%2Cc_limit/Apple-Watch-Ultra-2-Review-Featured-Gear.jpg' 
    },
    { 
      name: 'AirPods', 
      description: 'Wireless earbuds with seamless connectivity.', 
      image: 'https://media.wired.com/photos/618048b16fe08d62522d94c2/1:1/w_150%2Cc_limit/Gear-Apple-Airpods-3-2021-Review.jpg' 
    },
  ];

  return (
    <div>
      <h1>Apple Products</h1>
      <div className="product-list">
        {productList.map((product, index) => (
          <div key={index} className="product-item">
            <img src={product.image} alt={product.name} className="product-image" />
            <div className="product-details">
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AppleT;
