import React from 'react';
import { useCart } from './CartContext';
import './Mens.css'; // Import CSS file for styles
const Women = () => {
  const { addToCart } = useCart();
  const clothingList = [
    { 
      name: '5 pack of Dresses', 
      description: 'Casual and comfortable dresses for everyday wear.', 
      image: 'https://coveti.com/wp-content/uploads/2023/08/CAMILLA-STRAPLESS-MAXI-DRESS-VERDIS-WORLD-GHOST-1-2048x-zibeav-150x150.webp' 
    },
    { 
      name: '5 pack of Skirts', 
      description: 'Classic denim skirts suitable for various occasions.', 
      image: 'https://dressbarn.com/cdn/shop/files/flared-midi-skirt-with-pockets-252340.jpg?crop=center&height=150&v=1711149079&width=150' 
    },
    { 
      name: '10 pack of Hijabs', 
      description: 'Beautiful hijabs for a elegant look.', 
      image: 'https://www.alhannah.com/wp-content/uploads/2017/01/hi_1485_detail_hd-150x150.jpg' 
    },
    { 
      name: '10 pack of Shirts', 
      description: 'Stylish shirts suitable for various occasions.', 
      image: 'https://www.greatoutdoorshop.com/wp-content/uploads/2023/03/simms-womens-shirt-isle-long-sleeve-dragonfly-gulf-blue-45-1000x1000-1-150x150.jpg' 
    },
  ];

    return (
      <div>
        <h1>Women's Clothing</h1>
        <div className="clothing-list">
          {clothingList.map((clothing, index) => (
            <div key={index} className="clothing-item">
              <img src={clothing.image} alt={clothing.name} className="clothing-image" />
              <div className="clothing-details">
                <h3>{clothing.name}</h3>
                <p>{clothing.description}</p>
                <button onClick={() => addToCart(clothing)}>Add to Cart</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  export default Women;
