import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './styles/Signup.css';

function Signup() {
    const navigate = useNavigate();
    const [accountType, setAccountType] = useState('user');
    const [adminCode, setAdminCode] = useState('');
    const [adminCodeValidated, setAdminCodeValidated] = useState(false);
    const [signupValues, setSignupValues] = useState({
        firstName: '',
        lastName: '',
        userId: '',
        password: '',
        name: '',
    });
    const [isPasswordVisible, setIsPasswordVisible] = useState(false);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setSignupValues(prevValues => ({
            ...prevValues,
            [name]: value
        }));

        if (name === 'lastName' && accountType === 'admin') {
            setSignupValues(prevValues => ({
                ...prevValues,
                userId: `BosBuy${value}`
            }));
        }
    };

    const handleAdminCodeInput = (event) => {
        setAdminCode(event.target.value);
    };

    const validateAdminCode = () => {
        if (adminCode === 'ADMIN') {
            setAdminCodeValidated(true);
        } else {
            alert('Invalid Admin Code');
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const url = 'http://localhost:9000/createAccount';
        let data = {
            accountType,
            ...signupValues
        };

        if (accountType === 'admin') {
            data.adminCode = adminCode;
        } else if (accountType === 'user') {
            delete data.name;
        } else if (accountType === 'company') {
            delete data.firstName;
            delete data.lastName;
            delete data.userId;
            data.name = signupValues.name;
        }

        axios.post(url, data)
            .then((res) => {
                alert('Successfully Signed Up!');
                navigate('/login');
            })
            .catch((err) => {
                console.error(err);
                alert(`Error in Signing Up: ${err.response?.data?.message || 'An error occurred'}`);
            });
    };

    const togglePasswordVisibility = () => {
        setIsPasswordVisible(!isPasswordVisible);
    };

    return (
        <div className="signup">
            <h2>Signup</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="accountType">Account Type:</label>
                    <select id="accountType" value={accountType} onChange={(e) => setAccountType(e.target.value)}>
                        <option value="user">User</option>
                        <option value="company">Company</option>
                        <option value="admin">Admin</option>
                    </select>
                </div>
                {accountType === 'admin' && !adminCodeValidated && (
                    <div>
                        <label htmlFor="adminCode">Enter Admin Code:</label>
                        <input type="text" id="adminCode" value={adminCode} onChange={handleAdminCodeInput} />
                        <button type="button" onClick={validateAdminCode}>Validate Code</button>
                    </div>
                )}
                {adminCodeValidated && (
                    <>
                        {accountType !== 'company' && (
                            <>
                                <div>
                                    <label htmlFor="firstName">First Name:</label>
                                    <input type="text" id="firstName" name="firstName" value={signupValues.firstName} onChange={handleInputChange} />
                                </div>
                                <div>
                                    <label htmlFor="lastName">Last Name:</label>
                                    <input type="text" id="lastName" name="lastName" value={signupValues.lastName} onChange={handleInputChange} />
                                    <div>
                                        <label>Your Admin ID will be:</label>
                                        <span>{signupValues.userId}</span>
                                    </div>
                                </div>
                            </>
                        )}
                        <div>
                            <label htmlFor="password">Password:</label>
                            <input type={isPasswordVisible ? "text" : "password"} id="password" name="password" value={signupValues.password} onChange={handleInputChange} />
                            <button type="button" onClick={togglePasswordVisibility} className='showhide'>
                                {isPasswordVisible ? 'Hide' : 'Show'}
                            </button>
                        </div>
                        <button type="submit" className='signup-button'>Signup</button>
                    </>
                )}
            </form>
            <button onClick={() => navigate('/')} className='home-button'>Home</button>
            <button onClick={() => navigate('/login')} className='login-button'>Go to Login</button>
        </div>
    );
}

export default Signup;
