import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProductList() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:9000/getProducts');
            setProducts(response.data);
        } catch (error) {
            console.error('Failed to fetch products:', error);
        }
    };

    return (
        <div>
            <h1>Product List</h1>
            {products.map(product => (
                <div key={product._id} style={{ border: '1px solid gray', padding: '10px', margin: '10px' }}>
                    <img src={product.imageUrl} alt={product.name} style={{ width: '100px', height: '100px' }} />
                    <h2>{product.name}</h2>
                    <p>{product.description}</p>
                    <p>Price: ${product.price}</p>
                    <p>Sold by: {product.soldBy}</p>
                    <p>Category: {product.category} - {product.subcategory}</p>
                </div>
            ))}
        </div>
    );
}

export default ProductList;
