import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from './CartContext'; // Make sure this path is correct
import axios from 'axios';
import './styles/index.css';
import { useAuth } from './UserContext';  // Ensure this path is correct

function HomePage() {
  const { authState } = useAuth();
  const { addToCart } = useCart();
  const navigate = useNavigate();
  const [categories, setCategories] = useState({
    Grocery: [],
    Clothing: [],
    Technology: []
  });

  useEffect(() => {
    // Fetch random products for each category
    Object.keys(categories).forEach(category => {
      axios.get(`http://localhost:9000/products/random/${category}`)
        .then(response => {
          setCategories(prev => ({ ...prev, [category]: response.data }));
        })
        .catch(error => console.error('Failed to fetch products:', error));
    });
  }, []);

  const getWelcomeMessage = () => {
    if (!authState.user) return 'Welcome to the HomePage';
    if (authState.type === 'user') {
      return `Welcome ${authState.user.firstName} ${authState.user.lastName} to the HomePage`;
    }
    return `Welcome ${authState.user.name} to the HomePage`;
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>{getWelcomeMessage()}</h1>
      {Object.entries(categories).map(([category, products]) => (
        <div key={category}>
          <h2 onClick={() => navigate(`/${category.toLowerCase()}`)} style={{ cursor: 'pointer' }}>{category}</h2>
          <div style={{ display: 'flex', justifyContent: 'space-around' }}>
            {products.map(product => (
              <div key={product._id}>
                <img src={product.imageUrl} alt={product.name} style={{ width: '100px', height: '100px' }} />
                <h3>{product.name}</h3>
                <p>Price: ${product.price}</p>
                <button onClick={() => addToCart(product)}>Add to Cart</button>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default HomePage;
