import React from 'react';
import { useCart } from './CartContext';
import './Apple.css'; // Import CSS file for styles

const Micro = () => {
  const { addToCart } = useCart();
  const productList = [
    { 
      name: 'Laptops', 
      description: 'Powerful and sleek laptop for work and creativity.', 
      image: 'https://www.highspeedinternet.com/app/uploads/2019/12/Featured-BestLaptops-150x150.jpg' 
    },
    { 
      name: 'Tablets', 
      description: 'Versatile tablet for productivity and entertainment.', 
      image: 'https://mombasacomputers.b-cdn.net/wp-content/uploads/2021/01/Samsung-Galaxy-Tab-A-SM-T380-8.0-Inches-32-GB-Android-Tablet-4-150x150.jpg' 
    },

  ];

  return (
    <div>
      <h1>Microsoft Products</h1>
      <div className="product-list">
        {productList.map((product, index) => (
          <div key={index} className="product-item">
            <img src={product.image} alt={product.name} className="product-image" />
            <div className="product-details">
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Micro;
