import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import NavBar from './NavBar';
import HomePage from './HomePage';
import Login from './login';
import Signup from './signup';
import EditProfile from './EditProfile';
import ChangePassword from './ChangePassword';
import { UserProvider } from './UserContext';
import { CartProvider } from './CartContext';
import Cart from './Cart';
import Produce from './Produce'
import Meat from './Meat'
import Dairy from './Dairy'
import Mens from './Mens'
import Women from './Women'
import Kids from './Kids'
import Apple from './AppleT'
import Sam from './Sam'
import Micro from './Micro'

function Layout() {
  const location = useLocation();
  const noNavBarRoutes = ['/login', '/signup'];

  return (
    <div>
      {!noNavBarRoutes.includes(location.pathname) && <NavBar />}
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/homePage" element={<HomePage />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/groceries/produce" element={<Produce />} />
        <Route path="/groceries/meats" element={<Meat />} />
        <Route path="/groceries/dairies" element={<Dairy />} />
        <Route path="/clothing/men" element={<Mens />} />
        <Route path="/clothing/women" element={<Women />} />
        <Route path="/clothing/kids" element={<Kids />} />
        <Route path="/technology/apple" element={<Apple />} />
        <Route path="/technology/samsung" element={<Sam/>} />
        <Route path="/technology/microsoft" element={<Micro/>} />
        <Route path="/editProfile" element={<EditProfile/>} />
        <Route path="/changePassword" element={<ChangePassword/>} />
        <Route path="/cart" element={<Cart/>} />

      </Routes>
    </div>
  );
}

function App() {
  return (
    <Router>
      <UserProvider>
        <CartProvider>
          <Layout />
        </CartProvider>
      </UserProvider>
    </Router>
  );
}

export default App;
