import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './UserContext';
import { categories } from './categories';
import axios from 'axios';  
import Dropdown from './Dropdown';  
import './styles/NavBar.css'; // Updated import path

function NavBar() {
  const navigate = useNavigate();
  const { authState, handleSignout } = useAuth();  
  const [companyOptions, setCompanyOptions] = useState([]);

  useEffect(() => {
    const fetchCompanies = async () => {
        try {
            const response = await axios.get('http://localhost:9000/getCompanies');
            setCompanyOptions(response.data);
        } catch (error) {
            console.error('Failed to fetch companies:', error);
        }
    };
    fetchCompanies();
  }, []);

  return (
    <div id="NavBar">
      <div className="bosbuy-style">
        <span className="pinkBOSstyle">Bos</span>
        <span className="whiteBUYstyle">Buy</span>
      </div>
      <nav>
        <div className="spinning-style" onClick={() => navigate('/homepage')}>
          &#8962; {/* Home icon */}
        </div>
        <div className="nav-buttons">
          {authState.isAuthenticated && (
            <>
              <button onClick={() => navigate('/addProduct')}>Add Product</button>
              <button className="cart-button" onClick={() => navigate('/cart')} />
              <button onClick={() => navigate('/editProfile')}>Edit Profile</button>
              <button onClick={handleSignout}>Sign Out</button>
            </>
          )}
          {!authState.isAuthenticated && (
            <>
              <button onClick={() => navigate('/login')}>Login</button>
              <button onClick={() => navigate('/signup')}>Sign Up</button>
            </>
          )}
        </div>
        <div>
          <Dropdown label="Grocery" options={categories.Grocery} />
          <Dropdown label="Clothing" options={categories.Clothing} />
          <Dropdown label="Technology" options={categories.Technology} />
          <Dropdown label="Companies" options={companyOptions} />
          <button onClick={() => navigate('/products')}>Product Listing</button>
        </div>
      </nav>
    </div>
  );
}

export default NavBar;
