import React from 'react';
import { useCart } from './CartContext';
import './Mens.css'; // Import CSS file for styles

const Mens = () => {
  const { addToCart } = useCart();
  const clothingList = [
    { 
      name: '5 pack of T-Shirts', 
      description: 'Casual and comfortable clothing for everyday wear.', 
      image: 'https://thenobledandy.com/media/clothing/shirts/cordone-1956-blue-striped-twill-shirt-1-150x150.jpg' 
    },
    { 
      name: '5 pack of Jeans', 
      description: 'Classic denim pants suitable for various occasions.', 
      image: 'https://faworldentertainment.com/cdn/shop/products/2021_Hockey_QTR4_GraphicDetail_Apparel_DoubleKneeJean_Indigo_Front.png?v=1659638522&width=150' 
    },
    { 
      name: '10 pack of T-Shirts', 
      description: 'Casual and comfortable clothing for everyday wear.', 
      image: 'https://www.barbanerastyle.com/wp-content/uploads/2020/08/barbanera-plain-white-raw-cotton-t-shirt1-150x150.jpg' 
    },
    { 
      name: '10 pack of Jeans', 
      description: 'Classic denim pants suitable for various occasions.', 
      image: 'https://cdn.shopify.com/s/files/1/0847/9976/products/Def-OG-Hugh-Jean-Denim-Pant-Jeans-Washed-Blue_150x.jpg?v=1630543360' 
    },

  ];

  return (
    <div>
      <h1>Men's Clothing</h1>
      <div className="clothing-list">
        {clothingList.map((clothing, index) => (
          <div key={index} className="clothing-item">
            <img src={clothing.image} alt={clothing.name} className="clothing-image" />
            <div className="clothing-details">
              <h3>{clothing.name}</h3>
              <p>{clothing.description}</p>
              <button onClick={() => addToCart(clothing)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Mens;
