import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './UserContext';

function AddCardPage() {
    const navigate = useNavigate();
    const { authState } = useAuth();
    const [cardDetails, setCardDetails] = useState({
        cardNumber: '',
        cardName: '',
        expirationDate: '',
        cvv: '',
        zipCode: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCardDetails(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!cardDetails.cardNumber || !cardDetails.cardName || !cardDetails.expirationDate || !cardDetails.cvv || !cardDetails.zipCode) {
            alert('Please fill in all fields');
            return;
        }
        try {
            const response = await axios.post('http://localhost:9000/addCard', { ...cardDetails, userId: authState.user._id });
            if (response.status === 201) {
                alert('Card added successfully!');
                navigate('/editProfile');
            } else {
                alert('Failed to add card');
            }
        } catch (error) {
            console.error('Failed to add card:', error);
            alert('Failed to add card: ' + (error.response?.data?.message || error.message));
        }
    };

    return (
        <div className="add-card-page">
            <h2>Add Card</h2>
            <form onSubmit={handleSubmit}>
                {['cardNumber', 'cardName', 'expirationDate', 'cvv', 'zipCode'].map(field => (
                    <label key={field}>
                        {field}:
                        <input type="text" name={field} value={cardDetails[field]} onChange={handleChange} required />
                    </label>
                ))}
                <button type="submit">Add Card</button>
                <button type="button" onClick={() => navigate(-1)}>Back</button>
            </form>
        </div>
    );
}

export default AddCardPage;
