import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Signup.css';

function Signup() {
    const navigate = useNavigate();
    const [accountType, setAccountType] = useState('user');
    const [adminCode, setAdminCode] = useState('');
    const [signupValues, setSignupValues] = useState({
        firstName: '',
        lastName: '',
        userId: '',
        password: '',
        name: '',  // For company name
    });
    const [isPasswordVisible, setIsPasswordVisible] = useState(false);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setSignupValues(prevValues => ({
            ...prevValues,
            [name]: value
        }));

        if (name === 'lastName' && accountType === 'admin') {
            setSignupValues(prevValues => ({
                ...prevValues,
                userId: `BosBuy${value}`
            }));
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const url = 'http://localhost:9000/createAccount';
        let data = {
            accountType,
            ...signupValues
        };

        if (accountType === 'admin') {
            data.adminCode = adminCode; // Include the admin code in the request
        } else if (accountType === 'user') {
            delete data.name; // Remove company name for user accounts
        } else if (accountType === 'company') {
            delete data.firstName;
            delete data.lastName;
            delete data.userId;
            data.name = signupValues.name; // Use the company name field for companies
        }

        axios.post(url, data)
            .then((res) => {
                alert('Successfully Signed Up!');
                navigate('/login');
            })
            .catch((err) => {
                console.error(err);
                const errorMessage = err.response ? err.response.data.message : 'An error occurred';
                alert(`Error in Signing Up: ${errorMessage}`);
            });
    };

    const togglePasswordVisibility = () => {
        setIsPasswordVisible(!isPasswordVisible);
    };

    return (
        <div className="signup">
            <h2>Signup</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="accountType">Account Type:</label>
                    <select id="accountType" name="accountType" value={accountType} onChange={(e) => setAccountType(e.target.value)}>
                        <option value="user">User</option>
                        <option value="company">Company</option>
                        <option value="admin">Admin</option>
                    </select>
                </div>
                {accountType === 'admin' && (
                    <div>
                        <label htmlFor="adminCode">Admin Code:</label>
                        <input type="text" id="adminCode" name="adminCode" value={adminCode} onChange={(e) => setAdminCode(e.target.value)} />
                    </div>
                )}
                {accountType !== 'company' && (
                    <>
                        <div>
                            <label htmlFor="firstName">First Name:</label>
                            <input type="text" id="firstName" name="firstName" value={signupValues.firstName} onChange={handleInputChange} />
                        </div>
                        <div>
                            <label htmlFor="lastName">Last Name:</label>
                            <input type="text" id="lastName" name="lastName" value={signupValues.lastName} onChange={handleInputChange} />
                        </div>
                    </>
                )}
                {accountType !== 'user' && (
                    <div>
                        <label htmlFor="name">Company Name:</label>
                        <input type="text" id="name" name="name" value={signupValues.name} onChange={handleInputChange} />
                    </div>
                )}
                <div>
                    <label htmlFor="password">Password:</label>
                    <input type={isPasswordVisible ? "text" : "password"} id="password" name="password" value={signupValues.password} onChange={handleInputChange} />
                    <button type="button" onClick={togglePasswordVisibility} className='showhide'>
                        {isPasswordVisible ? 'Hide' : 'Show'}
                    </button>
                </div>
                <button type="submit" className='signup-button'>Signup</button>
            </form>
            <button onClick={() => navigate('/')} className='home-button'>Home</button>
            <button onClick={() => navigate('/login')} className='login-button'>Go to Login</button>
        </div>
    );
}

export default Signup;
