import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './UserContext';
import { categories } from './categories';
import axios from 'axios';  
import Dropdown from './Dropdown';  
import './styles/NavBar.css';

function NavBar() {
  const navigate = useNavigate();
  const { authState, handleSignout } = useAuth();  

  const groceryOptions = categories.Grocery;
  const clothingOptions = categories.Clothing;
  const techOptions = categories.Technology;
  const [companyOptions, setCompanyOptions] = useState([]);

  useEffect(() => {
    const fetchCompanies = async () => {
        try {
            const response = await axios.get('http://localhost:9000/getCompanies');
            setCompanyOptions(response.data);
        } catch (error) {
            console.error('Failed to fetch companies:', error);
        }
    };

    fetchCompanies();
}, []);

  const handleSignoutAndNavigate = () => {
    handleSignout();  
    navigate('/');    // Navigates to the homepage
  };

  const welcomeMessage = authState.isAuthenticated ? (
    `${authState.type === 'company' ? 'Company' : 'User'}: ${authState.user.firstName || authState.user.name}`
  ) : null;

  return (
    <div>
      {/* "BOSBUY" text */}
      <div style={{ textAlign: 'center', fontFamily: 'Poppins', fontSize: '24px', color: 'black', backgroundColor: '#3f2860' }}>
        <span style={{ color: '#ff69b4' }}>Bos</span><span style={{ color: 'white' }}>Buy</span>
      </div>

      {/* Navigation bar */}
      <nav style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '12px', backgroundColor: '#3f2860', position: 'relative' }}>
        {/* Home icon and Cart icon together */}
        <div style={{ display: 'flex', position: 'absolute', left: '10px', top: '5px' }}>
          <div onClick={() => navigate('/homepage')} style={{ fontSize: '36px', cursor: 'pointer', color: 'white', padding: '10px', userSelect: 'none' }}>
            &#8962;
          </div>
          {authState.isAuthenticated && (
            <div onClick={() => navigate('/cart')} style={{ fontSize: '36px', cursor: 'pointer', color: 'white', padding: '10px', userSelect: 'none' }}>
              &#128722;
            </div>
          )}
        </div>

        {/* User/Company welcome message */}
        <div style={{ alignSelf: 'flex-end', paddingBottom: '10px' }}>
          <span style={{ color: 'white', paddingRight: '20px' }}>{welcomeMessage}</span>
          <button onClick={handleSignoutAndNavigate}>Sign Out</button>
        </div>

        <div style={{ width: '100%', display: 'flex', justifyContent: 'center' }}>
          <Dropdown label="Grocery" options={groceryOptions} />
          <Dropdown label="Clothing" options={clothingOptions} />
          <Dropdown label="Technology" options={techOptions} />
          <Dropdown label="Companies" options={companyOptions} />
          <button onClick={() => navigate('/products')}>Product Listing</button>
        </div>
      </nav>
    </div>
  );
}

export default NavBar;
